select.action:
var demoItemArea = this.parent.findItemById("demo_item_area");
if (this.hasClass("category_item_expand")) {
    // 已经展开
    this.removeClass("category_item_expand");
    demoItemArea.addClass("hide_area");
} else {
    this.addClass("category_item_expand");
    demoItemArea.removeClass("hide_area");
}

item.action:
if (this.text == "反向") {
    this.text = "反向布局文本反向布局文本反向布局文本123";
} else {
    this.text = "反向";
}

var massiveArea = this.ownerPage.findItemById("massive-area");
var sliderArea = massiveArea.findParentById("slider-area");
var arrangement = massiveArea.getAttr("arrangement");
var addClass;
if ( arrangement == "grid" || arrangement == "grid-fixed" ) {
sliderArea.setAttr("direction", "horizontal");
addClass = "massive-cls-v-grid";
} else {
sliderArea.setAttr("direction", "vertical");
addClass = "massive-cls-v-linear";
}
massiveArea.removeClass("massive-cls-v-grid");
massiveArea.removeClass("massive-cls-h-grid");
massiveArea.removeClass("massive-cls-v-linear");
massiveArea.removeClass("massive-cls-h-linear");
massiveArea.addClass(addClass);
sliderArea.scrollTo(0, false);

