select.action:
this.dispatchEvent("onItemClick", "", "");
var DISPLAY_CLASS = "custom_render_item_display";

if ( this.getAttr("animation") == "enabled") {
    this.setAttr("animation", "disabled");
    this.setAttr("text", "");
    this.removeClass(DISPLAY_CLASS);
} else {
    this.setAttr("animation", "enabled");
}

logger.d("CUSTOM RENDER: ", "DISPLAY_CLASS is added.")
var DISPLAY_CLASS = "custom_render_item_display";
this.addClass(DISPLAY_CLASS);
this.setAttr("text", "自定义render测试文本");
